class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"new/comment",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$("#post-comments-"+e).prepend(o),t.deleteComment($(" .delete-comment-button",o)),new Togglelike($(".liker",o)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e._id}">\n                        <p>\n                            \n                            <small>\n                                <a class="delete-comment-button" href="new/delete/comment/${e._id}">X</a>\n                            </small>\n                            \n                            ${e.comments}\n                            <br>\n                            <small>\n                                ${e.user.name}\n                            </small>\n                            <div class="likes">\n                    <a class="likeanchor" href="/likes/toggle/?id=${e._id}&type=comments"  ><i class="fas fa-thumbs-up liker" ></i><p class="likecount"></p> Likes</a>\n                     </div>\n                        </p>    \n\n                </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}