class Togglelike{constructor(t){this.toggler=t,this.togglelike()}togglelike(){$(this.toggler).click((function(t){t.preventDefault();let e=$(this);e.toggleClass("liker-toggle");let l=$(this).parent().children(".likecount");$.ajax({type:"get",url:e.parent().attr("href")}).done((function(t){0==t.data.deleted?(l.text(++t.data.count),e.css("color","rgb(53, 109, 231)"),console.log("liked")):(l.text(--t.data.count),e.css("color","black"),console.log("unliked"))})).fail((function(t){console.log("error in completing request")}))}))}}class ToggleComment{constructor(t){this.toggler=t,this.togglecomment()}togglecomment(){$(this.toggler).click((function(t){t.preventDefault(),$(this).parent().children(".comments").toggleClass("commentshide")}))}}